import React from 'react';

function FinalVerdict({ score, verdict, summary }) {
  return (
    <div className="mt-6 p-4 bg-white dark:bg-gray-800 rounded-lg shadow">
      <h2 className="text-lg font-bold mb-2">Final Verdict</h2>
      <div className="flex items-baseline">
        <span className={`text-3xl font-bold ${verdict === 'Safe' ? 'text-green-600' : 'text-red-600'} mr-2`}>
          {verdict}
        </span>
        <span className="text-xl">{score}%</span>
      </div>
      <div className="mt-2 text-gray-700 dark:text-gray-300">{summary}</div>
    </div>
  );
}

export default FinalVerdict;
