import React, { useState, useEffect } from 'react';
import FailureExplorer from './components/FailureExplorer';
import FinalVerdict from './components/FinalVerdict';
import ClaimList from './components/ClaimList';
import { FaSun, FaMoon } from 'react-icons/fa';

function App() {
  const [theme, setTheme] = useState('');
  const [view, setView] = useState('explorer');
  const [answerText, setAnswerText] = useState('');
  const [claims, setClaims] = useState([]);
  const [finalScore, setFinalScore] = useState(0);
  const [finalVerdict, setFinalVerdict] = useState('');
  const [finalSummary, setFinalSummary] = useState('');
  const [reasons, setReasons] = useState([]);

  // Apply or remove 'dark' class on <html> based on theme state
  useEffect(() => {
    const root = window.document.documentElement;
    if (theme === 'dark') root.classList.add('dark');
    else root.classList.remove('dark');
  }, [theme]);

  // Simulate analysis by splitting the answer into claims
  const handleAnalyze = () => {
    // Split text into sentences (naive) and trim
    const parts = answerText.split(/\.(?!\d)/).map(s => s.trim()).filter(s => s);
    // Map each to a mock claim with rotating verdicts
    let simulatedClaims = parts.map((text, i) => {
      let verdict;
      if (i % 3 === 0) verdict = 'Supported';
      else if (i % 3 === 1) verdict = 'No Evidence';
      else verdict = 'Contradicted';
      return { text, verdict, evidence: [
        { docName: 'DocA', chunkId: 1, text: 'Excerpt from source A...' },
        { docName: 'DocB', chunkId: 2, text: 'Excerpt from source B...' }
      ] };
    });
    // Force a numeric contradiction example in first claim
    if (simulatedClaims.length > 0) {
      simulatedClaims[0].text = 'The model predicts that 30% of X have feature Y.';
      simulatedClaims[0].verdict = 'Contradicted';
      simulatedClaims[0].evidence = [
        { docName: 'Study_X', chunkId: 3, text: 'Study shows 18–22% of X have feature Y.' }
      ];
    }
    setClaims(simulatedClaims);

    // Compute a simple safety score and summary
    const supportedCount = simulatedClaims.filter(c => c.verdict === 'Supported').length;
    const score = Math.round((supportedCount / simulatedClaims.length) * 100);
    setFinalScore(score);
    setFinalVerdict(score >= 75 ? 'Safe' : 'Unsafe');
    setFinalSummary(
      score >= 75
        ? 'Answer appears largely supported by evidence.'
        : 'Answer contains contradictions or unsupported claims.'
    );
    // Generate bullet-point reasons
    let bulletPoints = [];
    simulatedClaims.forEach((c, i) => {
      if (c.verdict === 'Contradicted') {
        bulletPoints.push(`Claim ${i+1} contradicts referenced evidence.`);
      }
      if (c.verdict === 'No Evidence') {
        bulletPoints.push(`Claim ${i+1} lacks supporting evidence.`);
      }
    });
    if (score < 100) {
      bulletPoints.push('Multiple claims are not fully supported by valid sources.');
    }
    setReasons(bulletPoints);
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-gray-100">
      {/* Header with title and controls */}
      <header className="p-4 flex justify-between items-center bg-white dark:bg-gray-800 shadow">
        <h1 className="text-xl font-bold">
          {view === 'explorer' ? 'Failure Case Explorer' : 'LLM Answer Safety Report'}
        </h1>
        <div className="flex items-center space-x-4">
          {view === 'report' && (
            <button onClick={() => setView('explorer')} className="text-sm text-blue-500">
              Back to Explorer
            </button>
          )}
          {view === 'explorer' && (
            <button onClick={() => setView('report')} className="text-sm text-blue-500">
              Go to Safety Report
            </button>
          )}
          <button onClick={() => setTheme(theme === 'dark' ? '' : 'dark')}>
            {theme === 'dark' ? (
              <FaSun className="text-yellow-400" />
            ) : (
              <FaMoon className="text-gray-700" />
            )}
          </button>
        </div>
      </header>

      {/* Main content */}
      {view === 'explorer' ? (
        <FailureExplorer />
      ) : (
        <div className="p-4">
          {/* Textarea for user to paste model answer */}
          <textarea
            className="w-full p-2 mb-4 bg-white dark:bg-gray-800 border border-gray-300 dark:border-gray-600 rounded"
            rows={4}
            placeholder="Paste model-generated answer here..."
            value={answerText}
            onChange={e => setAnswerText(e.target.value)}
          />
          <button onClick={handleAnalyze} className="px-4 py-2 bg-blue-600 text-white rounded">
            Analyze Answer
          </button>

          {claims.length > 0 && (
            <>
              {/* Final Verdict */}
              <FinalVerdict score={finalScore} verdict={finalVerdict} summary={finalSummary} />

              {/* List of claims */}
              <ClaimList claims={claims} />

              {/* Why Unsafe reasons */}
              <div className="mt-6">
                <h3 className="text-xl font-semibold mb-2">Why this answer is unsafe</h3>
                <ul className="list-disc list-inside space-y-1 text-gray-700 dark:text-gray-300">
                  {reasons.map((r, i) => (
                    <li key={i}>{r}</li>
                  ))}
                </ul>
              </div>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
