import React from 'react';

function VerdictBadge({ verdict }) {
  let colorClasses = 'bg-gray-500 text-white';
  if (verdict === 'Supported') {
    colorClasses = 'bg-green-600 text-white';
  } else if (verdict === 'Contradicted') {
    colorClasses = 'bg-red-600 text-white';
  } else if (verdict === 'No Evidence') {
    colorClasses = 'bg-yellow-600 text-white';
  }
  return (
    <span className={'inline-block px-2 py-1 text-xs font-semibold rounded ' + colorClasses}>
      {verdict}
    </span>
  );
}

export default VerdictBadge;
