import React, { useState } from 'react';
import VerdictBadge from './VerdictBadge';

function ClaimItem({ claim, evidence })
 {
  const [open, setOpen] = useState(false);

  // Numeric contradiction check: extract all numbers from claim and evidence text
  let contradictionWarning = '';
  const claimNums = (claim.text.match(/\d+(\.\d+)?/g) || []).map(Number);
  const evidenceNums = claim.evidence.flatMap(e =>
    (e.text.match(/\d+(\.\d+)?/g) || []).map(Number)
  );
  // If claim has one number and evidence has a range
  if (claimNums.length === 1 && evidenceNums.length >= 2) {
    const c = claimNums[0];
    const minE = Math.min(...evidenceNums);
    const maxE = Math.max(...evidenceNums);
    if (c < minE || c > maxE) {
      contradictionWarning = `⚠ Numeric contradiction detected (${c}% vs ${minE}–${maxE}%)`;
    }
  }
  // If both have one number
  else if (claimNums.length === 1 && evidenceNums.length === 1 && claimNums[0] !== evidenceNums[0]) {
    contradictionWarning = `⚠ Numeric contradiction detected (${claimNums[0]} vs ${evidenceNums[0]})`;
  }

  return (
    <div className="bg-white dark:bg-gray-800 p-4 rounded shadow">
      <div className="flex justify-between items-start">
        <div className="flex-1 pr-4">
          <div className="font-medium">{claim.text}</div>
          {contradictionWarning && (
            <div className="text-yellow-500 text-sm mt-1">{contradictionWarning}</div>
          )}
          <div className="mt-2">
            <VerdictBadge verdict={claim.verdict} />
          </div>
        </div>
        {/* Toggle button for evidence */}
        <button onClick={() => setOpen(!open)}>
          {open ? '▲' : '▼'}
        </button>
      </div>
      {open && (
        <div className="mt-2 pl-2 border-l border-gray-300 dark:border-gray-600">
          {claim.evidence.map((e, i) => (
            <div key={i} className="mb-2">
              <div className="text-sm font-semibold">
                {e.docName} - Chunk {e.chunkId}
              </div>
              <div className="text-sm">{e.text}</div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default ClaimItem;
